import React from 'react';

class AddBuilding extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            code: "",
            address: "",
            coordinates: {
              latitude: 0,
              longitude: 0
        }
    }
    }
        nameUpdate() {
            const val = this.myValue.value;
		    console.log(val);
            console.log("test");

            //Here you will need to update the value of the filter with the value from the textbox
            //this.setState({
              //  name: val
            //})
        }
        render() {
        if(this.props.showNew){
            return(
                <div>


                        <p>Building Name</p>
                        <form>
                        <input 
                            type="text" 
                            placeholder="Type to Filter" 
                            ref = {(value)=>{this.myValue = value}}
                            onChange={this.nameUpdate.bind(this)}
                        />
                        </form>
                        <form>
                        <p>Building Code</p>
                        <input 
                            type="text" 
                            placeholder="Type code of new building" 
                            ref = {(value)=>{this.myValue = value}}
                        />
                        <p>Building Address</p>
                        <input 
                            type="text" 
                            placeholder="Type address of new building" 
                            ref = {(value)=>{this.myValue = value}}
                        />
                    </form>
                    <p
                    onClick={()=>this.props.newViewUpdate(false)}
                    >
                        Add this Building
                    </p>
                </div>
            );
        }
        else{
            return(
                <p
                    onClick={()=>this.props.newViewUpdate(true)}
                >
                    Add Building
                </p>
            )
        }
    }
}

export default AddBuilding;
